mapper.update:

UPDATE dem_score SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id, a.name, a.parent_id AS "parent.id", a.parent_ids, a.area_id AS "area.id", a.code, a.sort, a.type, a.grade, a.address, a.zip_code, a.master, a.phone, a.fax,
a.remarks, a.create_by AS "createBy.id", a.create_date, a.update_by AS "updateBy.id", a.update_date, a.del_flag, a.useable AS useable, a.primary_person AS "primaryPerson.id", a.deputy_person AS "deputyPerson.id" FROM sys_office a WHERE a.del_flag = #{DEL_FLAG_NORMAL}
and type !='无' and ISNULL(type)=0 and LENGTH(trim(type)) > 0
AND a.area_id = #{area.id}
AND a.type = #{type}
ORDER BY a.CODE

SELECT DISTINCT type AS "email" FROM sys_office a WHERE a.del_flag = #{DEL_FLAG_NORMAL} and type !='无' AND ISNULL(type) = 0 AND LENGTH(trim(type)) > 0
AND a.area_id = #{area.id}
ORDER BY a.type
